INSERT INTO BOARD (F_CATEGORY_ID, F_CATEGORY, F_LECTURE, F_TITLE, F_GRADE, F_CONTENTS, F_COUNT, F_BEST, F_ID, F_NAME, F_ATTACH_FILE_ORI, F_ATTACH_FILE_CRYPTO, F_REG_TIME) VALUES ('1', '어학 및 자격증', '해커스 Talk! 2주 만에 끝내는 일본어 필수 문법', '구매담당자를 위한 협상의 10계명_IGM 협상스쿨', '3', '<p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">총무와 구매 직무를 병행하다가 팀이 커진 이후로 구매팀으로 발령나고 많은 고민을 했습니다..</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">&nbsp;</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">다른 부분은 괜찮았는데 가격이랑 스펙 네고하고 담당자랑 얘기하는게 깊이 들어가니 한도 끝도 없더라구요</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">&nbsp;</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">일단 빨리 다듣고 협상 자리가 생기면 10계명대로 해보려고 합니다.</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">&nbsp;</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">지금까지는 강의 구성은 괜찮고 학습 효과도 클 것 같아요!!!!!!!!!!!!</p>                            <p><br></p>', 0, null, 'hackers', '해커스', '', '', '2021-02-23 23:41:56');
INSERT INTO BOARD (F_CATEGORY_ID, F_CATEGORY, F_LECTURE, F_TITLE, F_GRADE, F_CONTENTS, F_COUNT, F_BEST, F_ID, F_NAME, F_ATTACH_FILE_ORI, F_ATTACH_FILE_CRYPTO, F_REG_TIME) VALUES ('1', '어학 및 자격증', '해커스 Talk! 2주 만에 끝내는 일본어 필수 문법', '임유정의 스피치 트레이닝, Before & After', '2', '<p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">스피치에 항상 고민이라 눌러봤다가 효과가 있을까 싶어서 들어봤습니다.</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">&nbsp;</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">스피치에서 문제가 되는 상황부터 시작하여 발표에서 필요한 세세한 부분과 적용 가능한 부분들을 소개하였으며</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">&nbsp;</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">생각보다 이론이 많은 과정입니다. 그래서 그런지 실제로 연습해보고&nbsp;적용해보려고 노력하는데 쉽지는 않네요.</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">&nbsp;</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">온라인 수업은 첫 단추라고 생각하고 학원수업도 병행해서 열심히 해보려고 합니다!</p>                            <p><br></p>', 0, null, 'hackers', '해커스', '', '', '2021-02-23 23:42:16');
INSERT INTO BOARD (F_CATEGORY_ID, F_CATEGORY, F_LECTURE, F_TITLE, F_GRADE, F_CONTENTS, F_COUNT, F_BEST, F_ID, F_NAME, F_ATTACH_FILE_ORI, F_ATTACH_FILE_CRYPTO, F_REG_TIME) VALUES ('1', '어학 및 자격증', '해커스Talk! 왕초보 중국어', '신입사원용 커뮤니케이션 강의', '2', '<span style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">회사에서 필요한 전반적인 내용을 다룬게 좋았습니다. 신입사원이 회사에서 인정받는 이유는 여러가지가 있겠지만 그중에서도 기본이 되는 커뮤니케이션 능력을 갖추는 것은 필수라고 생각해서 해당 강의를 신청했습니다. 상사,동료,거래처와의 커뮤니케이션에서 지침으로 삼을만한 내용들이 있어서 열심히 들었습니다. 강의 추천합니다.</span>                            <p><br></p>', 0, null, 'hackers', '해커스', '', '', '2021-02-23 23:42:31');
INSERT INTO BOARD (F_CATEGORY_ID, F_CATEGORY, F_LECTURE, F_TITLE, F_GRADE, F_CONTENTS, F_COUNT, F_BEST, F_ID, F_NAME, F_ATTACH_FILE_ORI, F_ATTACH_FILE_CRYPTO, F_REG_TIME) VALUES ('1', '어학 및 자격증', '해커스Talk! 기본어휘 중국어', 'IT기기를 통한 업무 스킬 향상, 신입사원 역량강화 프로젝트', '2', '<p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">일하는 형태를 잡는데 도움이 되긴 하는데 내용이 많이 쉬워요</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">그것만 좀 아쉽고 꽤 도움이 됬습니당</p>                            <p><br></p>', 0, null, 'hackers', '해커스', '', '', '2021-02-23 23:42:48');
INSERT INTO BOARD (F_CATEGORY_ID, F_CATEGORY, F_LECTURE, F_TITLE, F_GRADE, F_CONTENTS, F_COUNT, F_BEST, F_ID, F_NAME, F_ATTACH_FILE_ORI, F_ATTACH_FILE_CRYPTO, F_REG_TIME) VALUES ('1', '어학 및 자격증', '[국가공인자격] ITQ 취득 대비(Excel 2010, PowerPoint 2010)', '처음 맡은 프로젝트 책임자...', '4', '<p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">안녕하세요. 저와 같이 프로젝트를 수행하고 계시는 분들게 도움이 되시라고 수강후기를 남깁니다...</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">&nbsp;</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">저는 중견기업에 재직하고 있는데 승진과 더불어 처음으로 프로젝트의 책임자가 되었습니다.</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">&nbsp;</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">정말 막막하고 사실 관련 문서도 어떻게 작성하는지 잘 몰라서</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">&nbsp;</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">빨리 뭔가 배울 수 있는 인터넷 강의를 신청하게 됬습니다.</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">&nbsp;</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">일단 프로젝트를 처음 맡은 실무자에게는 정말 좋은 내용이구요 (문서 작성, 인력 관리 등등)</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">&nbsp;</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">pmp 자격증 관련 공부와는 조금 거리가 있네요..</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">&nbsp;</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">좀 배우다보니 욕심이 생기는데 자격증 취득을 위한 과정도 만든다면 들을 용의가 있습니다.</p>                            <p><br></p>', 0, null, 'hackers', '해커스', '', '', '2021-02-23 23:43:03');
INSERT INTO BOARD (F_CATEGORY_ID, F_CATEGORY, F_LECTURE, F_TITLE, F_GRADE, F_CONTENTS, F_COUNT, F_BEST, F_ID, F_NAME, F_ATTACH_FILE_ORI, F_ATTACH_FILE_CRYPTO, F_REG_TIME) VALUES ('1', '어학 및 자격증', '누구나 쉽게 배우는 왕초보 영어 STEP1', '현장 에피소드로 풀어보는 멘토코치 카운셀링', '2', '<p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">해당 과정을 마치며...</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">직장이나 비즈니스 상황에서 벌어지는 다양한 내용을 다뤄서 너무 좋았습니다.</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">일단 강의를 처음 들을 땐 바빠서 집중하지 않았는데 실제로 도움이 되는것 같아서 점차 열심히 들었습니다</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">역시 일에 연관된 거나 아니면 직장생활 관련 강의는 지속적으로 들어봐야 겠다는 생각과 근래 생활에 대한 반성을 하게되네요..</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">아, 그리고 성의있는 첨삭도 감사합니닷</p>                            <p><br></p>', 0, null, 'hackers', '해커스', '', '', '2021-02-23 23:43:38');
INSERT INTO BOARD (F_CATEGORY_ID, F_CATEGORY, F_LECTURE, F_TITLE, F_GRADE, F_CONTENTS, F_COUNT, F_BEST, F_ID, F_NAME, F_ATTACH_FILE_ORI, F_ATTACH_FILE_CRYPTO, F_REG_TIME) VALUES ('1', '어학 및 자격증', '누구나 쉽게 배우는 왕초보 영어 STEP2', '글로벌 기업처럼 일하는 방식을 혁신하라', '5', '<p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px; text-align: justify;">실제 알고 있던 글로벌 기업들의 사례를 통해서 혁신과 그들의 혁신 방업에 대해 배웠습니다.</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px; text-align: justify;">어떤것이 진짜 혁신인지 체감할 수 있었고 이를 통해 저도 사소한 것부터 바꿔서 업무에 적용해보려고 합니다.</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px; text-align: justify;">감사합니다.</p>                            <p><br></p>', 0, null, 'hackers', '해커스', '', '', '2021-02-23 23:43:54');
INSERT INTO BOARD (F_CATEGORY_ID, F_CATEGORY, F_LECTURE, F_TITLE, F_GRADE, F_CONTENTS, F_COUNT, F_BEST, F_ID, F_NAME, F_ATTACH_FILE_ORI, F_ATTACH_FILE_CRYPTO, F_REG_TIME) VALUES ('1', '어학 및 자격증', '미생으로 배우는 비즈니스 영어 - 실무영어', '제조와 생산에 근무하고 있는 분들을 위한 기초 강의!', '2', '<p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">사실 TOC라는 말도 잘 몰랐는데 이번 과정을 통해 배우게 되었습니다.</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">생산관리와 관련하여 기본적이지만 가장 중요한 내용들을 알차게 모아놔서</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">이 강의 하나만 봐도 생산관리의 기초는 어느정도 배웠다고 자신할 수 있지 않나 생각합니다.</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">제조, 생산쪽에서 근무하고 계신 분이라면 ''TOC 생산관리'' 강의를 통해</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">실무에서도 많이 도움이 될 수 있는 지식을 얻어가시기를 바랍니다.</p>                            <p><br></p>', 0, null, 'hackers', '해커스', '', '', '2021-02-23 23:44:11');
INSERT INTO BOARD (F_CATEGORY_ID, F_CATEGORY, F_LECTURE, F_TITLE, F_GRADE, F_CONTENTS, F_COUNT, F_BEST, F_ID, F_NAME, F_ATTACH_FILE_ORI, F_ATTACH_FILE_CRYPTO, F_REG_TIME) VALUES ('1', '어학 및 자격증', '미생으로 배우는 비즈니스 영어 - 일상회화', '오랜 금융권 실무로 쌓아올린 깊은 노하우의 강사님!', '2', '<p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px; line-height: 1.5;"><span style="font-family: Arial, sans-serif;">강사분께서 금융권에 오래 계셨던 분이기도 해서 많은 노하우를 배울 수 있었습니다.</span></p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px; line-height: 1.5;"><span style="font-family: Arial, sans-serif;">내가 잘 따라갈 수 있을 지, 시간이 부족한 것은 아닐지 걱정도 많이했지만</span></p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px; line-height: 1.5;"><span style="font-family: Arial, sans-serif;">강의를 들으면서 실제로 어려워서 고생했던 부분은 많이 없었고</span></p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px; line-height: 1.5;"><span style="font-family: Arial, sans-serif;">교강사선생님들께서도 과제첨삭이나 질문에 적극적으로 응해주셔서</span></p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px; line-height: 1.5;"><span style="font-family: Arial, sans-serif;">투자와 관련해 여러 이론과 능력을 키울 수 있었습니다.</span></p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px; line-height: 1.5;"><span style="font-family: Arial, sans-serif;">좋은 강의 제공해주셔서 감사드립니다.</span></p>                            <p><br></p>', 0, null, 'hackers', '해커스', '', '', '2021-02-23 23:44:33');
INSERT INTO BOARD (F_CATEGORY_ID, F_CATEGORY, F_LECTURE, F_TITLE, F_GRADE, F_CONTENTS, F_COUNT, F_BEST, F_ID, F_NAME, F_ATTACH_FILE_ORI, F_ATTACH_FILE_CRYPTO, F_REG_TIME) VALUES ('1', '어학 및 자격증', '미생으로 배우는 비즈니스 영어 - 회의/보고/발표하기', '무역강의 중 상급 강의! 글로벌 시대 무역전문가 강의!', '3', '<p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px; line-height: 1.8;"><span style="font-family: Batang, 바탕, serif;">영업쪽에 있으면서 무역을 공부하게 될 줄은 상상도 못했는데</span></p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px; line-height: 1.8;"><span style="font-family: Batang, 바탕, serif;">몇 년 몸담고 있다보니 결국 영업이란게 무역과 다를 게 없다는 것을 알게되었습니다.</span></p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px; line-height: 1.8;"><span style="font-family: Batang, 바탕, serif;">특히, 만약 해외영업쪽에 몸담고 계시는 분이라면 저보다 더 많은 것을 이 강의를 통해</span></p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px; line-height: 1.8;"><span style="font-family: Batang, 바탕, serif;">얻어가실 수 있을 거라 생각합니다.</span></p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px; line-height: 1.8;"><span style="font-family: Batang, 바탕, serif;">강의내용이 실무에서 자주 쓰는 정보들로 채워져있고, 무엇보다도 큰 흐름을 통해</span></p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px; line-height: 1.8;"><span style="font-family: Batang, 바탕, serif;">방향성을 제시해주니 무역관련 강의중에서는 상급에 속하는 강의가 아닌가 싶네요.</span></p>                            <p><br></p>', 0, null, 'hackers', '해커스', '', '', '2021-02-23 23:44:50');
INSERT INTO BOARD (F_CATEGORY_ID, F_CATEGORY, F_LECTURE, F_TITLE, F_GRADE, F_CONTENTS, F_COUNT, F_BEST, F_ID, F_NAME, F_ATTACH_FILE_ORI, F_ATTACH_FILE_CRYPTO, F_REG_TIME) VALUES ('1', '어학 및 자격증', '미생으로 배우는 비즈니스 영어 - 사교/인맥', '업무의 효율까지 높일 수 있는 원가절감 프로세스', '3', '<p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px; line-height: 1.8;"><span style="font-family: Dotum, 돋움, sans-serif;">원가절감 강의는 꼭 경영지원팀을 대상으로 한 강의는 아닌 것 같습니다.</span></p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px; line-height: 1.8;"><span style="font-family: Dotum, 돋움, sans-serif;">기업에 몸담고 있는 누구라도 원가절감을 공부한다면 회사의 입장에서 볼 땐</span></p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px; line-height: 1.8;"><span style="font-family: Dotum, 돋움, sans-serif;">큰동력이 될 수 있다고 생각합니다.</span></p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px; line-height: 1.8;"><span style="font-family: Dotum, 돋움, sans-serif;">물록 개개인에게 있어서도 업무의 효율을 높일 수 있다고 생각하는데,</span></p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px; line-height: 1.8;"><span style="font-family: Dotum, 돋움, sans-serif;">불필요한 지출을이나 프로세스를 줄임으로써 비용때문에 머리 싸매고&nbsp;끙끙 앓아야 하는 일을</span></p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px; line-height: 1.8;"><span style="font-family: Dotum, 돋움, sans-serif;">사전에 방지할 수도 있다고 생각하기 때문입니다.</span></p>                            <p><br></p>', 0, null, 'hackers', '해커스', '', '', '2021-02-23 23:45:08');
INSERT INTO BOARD (F_CATEGORY_ID, F_CATEGORY, F_LECTURE, F_TITLE, F_GRADE, F_CONTENTS, F_COUNT, F_BEST, F_ID, F_NAME, F_ATTACH_FILE_ORI, F_ATTACH_FILE_CRYPTO, F_REG_TIME) VALUES ('1', '어학 및 자격증', '그래머 게이트웨이 인터미디엇', '무역업 종사자라면 한 번 이상은 들어보아야 할 강의!', '3', '<p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">실무에서 어려움을 겪고 계신 무역업 현업자에게 추천하는 강의입니다.</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">매 년 규정변화같은 것은 수시로 알아서 공부를 해야 하기는하지만</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">이번 강의를 통해서 무역실무쪽의 전반적인 큰 흐름을 이해했습니다.</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">계약, 환율, 관세환급, 클레임 등 현업에서 일하면서 직접적으로 연관된 분야들을</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">알짜배기로 골라서 가르쳐주니 개인적으로는 제 돈을 주고 듣는다 해도</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">아깝지 않을 정도로 큰 도움을 받은 강의였습니다.</p>                            <p><br></p>', 0, null, 'hackers', '해커스', '', '', '2021-02-23 23:45:24');
INSERT INTO BOARD (F_CATEGORY_ID, F_CATEGORY, F_LECTURE, F_TITLE, F_GRADE, F_CONTENTS, F_COUNT, F_BEST, F_ID, F_NAME, F_ATTACH_FILE_ORI, F_ATTACH_FILE_CRYPTO, F_REG_TIME) VALUES ('1', '어학 및 자격증', '해커스 Talk! Medical English 고급', '채권 이해에 많은 도움이 된 강의', '2', '<p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px; line-height: 1.8;"><span style="font-family: Gulim, 굴림, AppleGothic, sans-serif;">금융쪽은 워낙 복잡해&nbsp;</span><span style="font-family: Gulim, 굴림, AppleGothic, sans-serif;">지금껏 살아오면서 공부를 많이 한&nbsp;적이 없습니다.</span></p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px; line-height: 1.8;"><span style="font-family: Gulim, 굴림, AppleGothic, sans-serif;">그러다가 공기업 금융권으로 이직을 결심하면서 미리 준비를 해야겠다고 생각했습니다.</span></p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px; line-height: 1.8;"><span style="font-family: Gulim, 굴림, AppleGothic, sans-serif;">책을 보면서 독학하는건 영 체질에 맞지 않는지라 인터넷 강의를 선택했는데</span></p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px; line-height: 1.8;"><span style="font-family: Gulim, 굴림, AppleGothic, sans-serif;">이번 강의를 보면서 채권쪽에 대해서 공부를 많이 할 수 있었습니다.</span></p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px; line-height: 1.8;"><span style="font-family: Gulim, 굴림, AppleGothic, sans-serif;">혹시 금융권으로 이직을 준비하거나, 금융권 신입사원분이 계시다면 이 강의 한 번쯤 들어보시는 걸 추천드립니다.</span></p>                            <p><br></p>', 0, null, 'hackers', '해커스', '', '', '2021-02-23 23:45:55');
INSERT INTO BOARD (F_CATEGORY_ID, F_CATEGORY, F_LECTURE, F_TITLE, F_GRADE, F_CONTENTS, F_COUNT, F_BEST, F_ID, F_NAME, F_ATTACH_FILE_ORI, F_ATTACH_FILE_CRYPTO, F_REG_TIME) VALUES ('2', '공통역량', '성과창출로 이어지는 대화의 기술 [조직 커뮤니케이션]', '주식초보자에게 큰 도움이 되었던 강의', '2', '<p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">금융권에 취직하고 공부할 게 더 많아졌다는 생각이 듭니다.</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">내 돈도 아니고 타인의 돈을 직접적으로 터치하는 직업이다보니 조심스러운데 실적은 필요하고...</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">많은 분들이 금융권에 대한 환상이 있고 그게 어느정도 맞는 만큼 사실 그만큼의 공부도 필요합니다.</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">금융 자격증같은게 엄청 많았는데 전 기초부터 닦는게 더 필요한 것 같아 주식투자, 보험 등과 관련된 단과 수업을 들었습니다.</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">그 중 파이낸셜씽킹은 강의 구성이 주식 초보자라도 쉽게 이해할 수 있도록 잘 짜여져있어 많은 도움이 되었습니다.</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">앞으로도 조금씩 단계를 높여가면서 AFPK나 투자권유자문인력도 해봐야 겠네요.</p>                            <p><br></p>', 0, null, 'hackers', '해커스', '', '', '2021-02-23 23:46:24');
INSERT INTO BOARD (F_CATEGORY_ID, F_CATEGORY, F_LECTURE, F_TITLE, F_GRADE, F_CONTENTS, F_COUNT, F_BEST, F_ID, F_NAME, F_ATTACH_FILE_ORI, F_ATTACH_FILE_CRYPTO, F_REG_TIME) VALUES ('2', '공통역량', '[직장생활백서] 관계가 어려운 당신에게', '효율적이고 실용적인 재무 기초를 위한 과정', '2', '<p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">대학교 때 배우기는 했지만 주로 마케팅쪽으로 공부를 많이해서 재무회계쪽은 잘 몰랐는데</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">이번 수업덕분에 많은 부분에서 도움을 받았습니다.</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">기초 수업인지라 큰 부담 없이 들을 수 있고, 실무에서도 많은 도움을 얻을 수 있습니다.</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">16강밖에 안 된다고 너무 적다고 생각하는 분들이 계실 수도 있지만</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">이 16강이 재무기초의 모든 것을 담고 있다는 것을 생각하면</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">매우 효율적이고, 무엇보다도 실용적인 과정이라고 생각합니다.</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">재무회계쪽은 익혀두면 앞으로 활동할 수 있는 범위가 더 커질 수 있으니 많은 분들에게 추천하는 강의입니다.</p>                            <p><br></p>', 0, null, 'hackers', '해커스', '', '', '2021-02-23 23:46:39');
INSERT INTO BOARD (F_CATEGORY_ID, F_CATEGORY, F_LECTURE, F_TITLE, F_GRADE, F_CONTENTS, F_COUNT, F_BEST, F_ID, F_NAME, F_ATTACH_FILE_ORI, F_ATTACH_FILE_CRYPTO, F_REG_TIME) VALUES ('2', '공통역량', '신입사원 상식사전(일반)', '사례로 배워서 더 알기 쉬운 교통사고 관련 강의', '2', '<span style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">보험쪽에서 근무하면서 교통사고 관련 법률은 조금 알아두면 좋다는 선배님 말에</span><br style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;"><span style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">''사례로 풀어보는 교통사고 처리방법'' 강의를 들었습니다.</span><br style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;"><span style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">딱딱한 이론만 나오는 수업은 싫어서 사례 위주로 수업하는 강의를 선택한 거였는데</span><br style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;"><span style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">사례만으로 수업을 했어도 정말 도움이 많이 되는 강의였습니다.</span><br style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;"><span style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">보험회사 다니는 사람한테도 잘 맞는 강의지만</span><br style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;"><span style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">꼭 그렇지 않더라도 일반 사람들도 만약 교통사고를 당했을 때 경험같은 지식으로</span><br style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;"><span style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">많은 도움을 얻을수 있지 않을까 생각합니다.</span>                            <p><br></p>', 0, null, 'hackers', '해커스', '', '', '2021-02-23 23:47:04');
INSERT INTO BOARD (F_CATEGORY_ID, F_CATEGORY, F_LECTURE, F_TITLE, F_GRADE, F_CONTENTS, F_COUNT, F_BEST, F_ID, F_NAME, F_ATTACH_FILE_ORI, F_ATTACH_FILE_CRYPTO, F_REG_TIME) VALUES ('2', '공통역량', '내 인생의 터닝포인트가 될 16가지 철학 이야기', '실무에서도 많은 도움이 될 것 같은 강의!', '3', '<p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px; line-height: 1.8;">중소기업에서 일하면서 건설 관련 법적인 지식도 필요했는데<br>이번 강의덕분에 어렵지 않게 기초를 다질 수 있었습니다.<br>강의도 16개 강의에 저한테 필요한 내용들로 사례위주로 꽉꽉 채워서<br>수강하기 쉬웠으며, 복습기간 한 달이 더 주어져서 어려웠던 부분은 한 번씩 더 들었습니다.</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px; line-height: 1.8;">건설업에서 일하고 계신 분이나, 이 쪽으로 막 들어온 신입사원은<br>이 강의는 기본적으로 들어둔다면 앞으로 실무에서도 많은 도움이 될 것 같습니다.</p>                            <p><br></p>', 0, null, 'hackers', '해커스', '', '', '2021-02-23 23:47:27');
INSERT INTO BOARD (F_CATEGORY_ID, F_CATEGORY, F_LECTURE, F_TITLE, F_GRADE, F_CONTENTS, F_COUNT, F_BEST, F_ID, F_NAME, F_ATTACH_FILE_ORI, F_ATTACH_FILE_CRYPTO, F_REG_TIME) VALUES ('2', '공통역량', '개인정보 보호 교육', '코딩 입문을 위한 강의로 추천합니다.', '2', '<span style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">요즘 애들은 초등학교(?) 부터 코딩을 배운다고 하길래</span><br style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;"><span style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">저도 자기개발 겸 컴퓨터 관련 공부를 시작했습니다.</span><br style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;"><span style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">처음에는 수업을 하면서 익숙하지 않은 용어들이 나와 힘들기도 했지만</span><br style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;"><span style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">인터넷 찾아보면서 공부하다보니 다행히 금방 익숙해질 수 있었습니다.</span><br style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;"><span style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">용어가 낯설어서 그렇지 수업 자체는 이해하기 쉽게 진행되었으며</span><br style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;"><span style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">복습기간도 주어져서 최소한 기초는 다질 수 있었다고 생각합니다.</span><br style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;"><span style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">코딩에 대해서 초보 입문하는 분들이라면 이 강의로 입문하시기를 추천합니다.</span>                            <p><br></p>', 0, null, 'hackers', '해커스', '', '', '2021-02-23 23:47:44');
INSERT INTO BOARD (F_CATEGORY_ID, F_CATEGORY, F_LECTURE, F_TITLE, F_GRADE, F_CONTENTS, F_COUNT, F_BEST, F_ID, F_NAME, F_ATTACH_FILE_ORI, F_ATTACH_FILE_CRYPTO, F_REG_TIME) VALUES ('2', '공통역량', '[대신읽어드립니다] 독서로 만나는 인문학', '김학도씨가 반가운 강의였습니다. ㅎㅎ', '4', '<p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">연예인이 강의에 나오니까 뭔가 신선하고 재밌었습니다.</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">내용 자체는 도움되는 면도 있고, 살짝 어수선한 면도 있었습니다.</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">경영전략,, 참 많이 배울 수록 써먹을 데는 많은 것 같습니다.</p>                            <p><br></p>', 0, null, 'hackers', '해커스', '', '', '2021-02-23 23:47:59');
INSERT INTO BOARD (F_CATEGORY_ID, F_CATEGORY, F_LECTURE, F_TITLE, F_GRADE, F_CONTENTS, F_COUNT, F_BEST, F_ID, F_NAME, F_ATTACH_FILE_ORI, F_ATTACH_FILE_CRYPTO, F_REG_TIME) VALUES ('2', '공통역량', '예술작품 2배 즐기기! 유럽미술관 속 명화 이야기', '브랜드 전략의 중요성', '5', '<p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">루이비통의 사례를 들어 좋은 내용을 배웠습니다. 가치를 담는 브랜드 전략,</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">이런 내용은 회사 생활에서뿐 아니라, 나중에 개인 사업을 할 때도 유용하게 생각해볼 필요가 있겠네요.</p>                            <p><br></p>', 0, null, 'hackers', '해커스', '', '', '2021-02-23 23:48:16');
