INSERT INTO BOARD (F_CATEGORY_ID, F_CATEGORY, F_LECTURE, F_TITLE, F_GRADE, F_CONTENTS, F_COUNT, F_BEST, F_ID, F_NAME, F_ATTACH_FILE_ORI, F_ATTACH_FILE_CRYPTO, F_REG_TIME) VALUES ('2', '공통역량', '[꿈꾸는 책방] 그림책이 들려주는 힐링-★올인원 패키지★', '앱 개발의 기초를 이해할수있는 강의입니다.', '3', '<p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">앱디자이너로 활동하면서, 항상 앱개발 프로세스에 대한 이해가 부족했었는데,</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">이 강의를 듣고 그 기초를 학습할 수 있는 계기가 되어 좋았습니다.</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">타이젠을 통한 개발환경을 구축하고, 여러가지 유용한 위젯을 활용하는 방법까지 배울수있었습니다.</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">이 컨텐츠를 통해 조금만 더 실습하고 익히다보면 혼자서도 앱컨텐츠를 개발할수 있겠다는 생각이 들었습니다.</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">저와같은 앱개발 입문자들에게도 추천드리고 싶습니다.</p>                            <p><br></p>', 0, null, 'hackers', '해커스', '', '', '2021-02-23 23:48:47');
INSERT INTO BOARD (F_CATEGORY_ID, F_CATEGORY, F_LECTURE, F_TITLE, F_GRADE, F_CONTENTS, F_COUNT, F_BEST, F_ID, F_NAME, F_ATTACH_FILE_ORI, F_ATTACH_FILE_CRYPTO, F_REG_TIME) VALUES ('2', '공통역량', '[꿈꾸는 책방] 그림책이 들려주는 육아 힐링 라이프', '디지털 콘텐츠를 전반적으로 이해할수 있는 계기가 되었습니다.', '4', '<p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">디지털 콘텐츠에 대해 이해하기 위해 학습자료를 찾던도중,&nbsp;이 강의를 접하게 되었습니다.</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">저와같은 초보자들이 학습하기 적합한, 기본적인 정보부터 다양한 실습을 통해 학습한 내용을 적용해보는 점이 마음에 들었습니다.</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">여기서 배운데로 현재 기획하고 있는 컨텐츠를 인디자인으로 제작해보았는데, 참 뿌듯한 느낌이었습니다.</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">다른 디지털 콘텐츠 입문자분들께도 추천해드리고 싶습니다.</p>                            <p><br></p>', 0, null, 'hackers', '해커스', '', '', '2021-02-23 23:49:05');
INSERT INTO BOARD (F_CATEGORY_ID, F_CATEGORY, F_LECTURE, F_TITLE, F_GRADE, F_CONTENTS, F_COUNT, F_BEST, F_ID, F_NAME, F_ATTACH_FILE_ORI, F_ATTACH_FILE_CRYPTO, F_REG_TIME) VALUES ('2', '공통역량', '[꿈꾸는 책방] 그림책이 들려주는 직장인 힐링 라이프', '어렵게만 느껴졌던 문법공부가 재미있어졌어요!', '3', '<p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">문법공부라면 따분함부터 느꼈는데, 그래머 게이트웨이 베이직을 수강하고 나서는 자신감을 얻었습니다.</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">우리 생활에서 바로 사용할 수 있는 문장과 다양한 유형의 연습문제를 통해&nbsp;재미있으면서도 기초를 탄탄히 다지는 기회가</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">되었습니다. 저와같은, 처음 시작하는 초보 학습자들도 문법에 대한 두려움을 벗어났으면 하는 바램입니다.</p>                            <p><br></p>', 0, null, 'hackers', '해커스', '', '', '2021-02-23 23:49:27');
INSERT INTO BOARD (F_CATEGORY_ID, F_CATEGORY, F_LECTURE, F_TITLE, F_GRADE, F_CONTENTS, F_COUNT, F_BEST, F_ID, F_NAME, F_ATTACH_FILE_ORI, F_ATTACH_FILE_CRYPTO, F_REG_TIME) VALUES ('2', '공통역량', '[꿈꾸는 책방] 그림책이 들려주는 직장인 힐링 라이프', '문서작성의 기초를 다질 수 있게 해준 강의!', '3', '<p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px; line-height: 1.8;">음대나와서 기간제&nbsp;선생님을 하다가 운이 좋게 공연장 기획자 자리에 계약직으로 들어왔어요.</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px; line-height: 1.8;">학교다닐 때 문서작성하거나 그럴 일이 없다보니 피피티나 엑셀같은게 진짜 낯설었어요ㅠㅠ</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px; line-height: 1.8;">문서작성때문에 사수한테 많이 혼났는데, 친구가 근로자카드로 수업 들을게 많다고 해서 문서작성과정 알아봤어요.</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px; line-height: 1.8;">처음엔 OA가 뭔지도 몰라서 이것저것 물어보면서 수업을 선택해서 ITQ 마스터 과정을 들었는데</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px; line-height: 1.8;">강의 들으면서 따라하다보니 회사에서 하는 기본적인 문서작성은 문제없이 할 수 있게 됐네요~</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px; line-height: 1.8;">이게 자격증 과정이기는 한데 전 자격증때문에 들었던 건 아니고, 워드나 엑셀좀 다루려고 들었는데</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px; line-height: 1.8;">도움 진짜 많이 받았습니다. 알아보니까 자격증시험도 많이 어려운건 아니라니까 도전해 봐도 괜찮을 것 같아요.</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px; line-height: 1.8;">문서작성때문에 회사에서 힘든 분들한테 추천할게요!!!</p>                            <p><br></p>', 0, null, 'hackers', '해커스', '', '', '2021-02-23 23:49:51');
INSERT INTO BOARD (F_CATEGORY_ID, F_CATEGORY, F_LECTURE, F_TITLE, F_GRADE, F_CONTENTS, F_COUNT, F_BEST, F_ID, F_NAME, F_ATTACH_FILE_ORI, F_ATTACH_FILE_CRYPTO, F_REG_TIME) VALUES ('2', '공통역량', '직장인 힐링 클래스: 나에게 선물하는 클래식 테라피', '회계! 어렵지 않게 배울 수 있었습니다!', '2', '<p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px; line-height: 1.8;">4학년 때 취업때문에 고민하다가 모교출신&nbsp;특별 채용하는 그런 거에 지원해서 어쩌다보니 회계쪽 업무를 하게 됐습니다.</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px; line-height: 1.8;">고등학교 때에도 숫자란 숫자는 다 싫어서 수포도 했었고, 선택과목도 경제쪽 이런데는 관심도 없었는데</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px; line-height: 1.8;">직장 다니려면 어쩔 수 없이 해야겠떠라구요. 회사에서 혼나는 것도 처음 몇 주지...</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px; line-height: 1.8;">다행히 요즘에는 회계 관련된 기초과정이 좀 있어서 알아보다가 공짜로 배울 수 있는 국가지원프로그램이 있다고 해서</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px; line-height: 1.8;">복습기간까지 포함해 2개월동안 수업들었습니다. 1급 바로 들어도 초보자도 어렵지 않다고 해서 이 과정 선택했구요.</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px; line-height: 1.8;">실제로 수업 자체도 설명해 주시는 개념들만 잘 알아두면 어렵다 할만한 부분은 없었습니다.</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px; line-height: 1.8;">다행이 요즘은 업무할 때 버벅거리는 게 많이 없어졌고, 인터넷 찾아보고 선배님께 질문하는게 많이 줄어</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px; line-height: 1.8;">야근하는 많이 안 해요. 지금&nbsp;회계일을 하는 분이 아니더라도 들어두면 나중에&nbsp;이직하거나 할 때, 선택권이 폭넓을 거라고 생각합니다.</p>                            <p><br></p>', 0, null, 'hackers', '해커스', '', '', '2021-02-23 23:50:06');
INSERT INTO BOARD (F_CATEGORY_ID, F_CATEGORY, F_LECTURE, F_TITLE, F_GRADE, F_CONTENTS, F_COUNT, F_BEST, F_ID, F_NAME, F_ATTACH_FILE_ORI, F_ATTACH_FILE_CRYPTO, F_REG_TIME) VALUES ('3', '일반직무', '왕초보도 따라하는 재테크 성공의 비밀', '서비스 업종에 종사하는 분들 화이팅입니다.', '4', '<p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">서비스업종에 종사하시는 분들 대다수는 정말 스트레스 많이 받으실거라고 생각합니다.</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">뭐 강의 하나 듣는다고 일하면서 모든 스트레스가 사라진다고 볼 수는 없겠지만</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">그래도 작은 팁같은 것들 많이 알아두고, 응대할 수 있는 마음가짐을 미리 다져놓는다면</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">실생활에서 받는 스트레스나 업무강도는 한 층 낮아질 수 있다고 생각합니다.</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">모두 화이팅 하시길</p>                            <p><br></p>', 0, null, 'hackers', '해커스', '', '', '2021-02-23 23:50:30');
INSERT INTO BOARD (F_CATEGORY_ID, F_CATEGORY, F_LECTURE, F_TITLE, F_GRADE, F_CONTENTS, F_COUNT, F_BEST, F_ID, F_NAME, F_ATTACH_FILE_ORI, F_ATTACH_FILE_CRYPTO, F_REG_TIME) VALUES ('3', '일반직무', '4차 산업혁명과 인사노무관리 이슈', '사례 교육이 참 좋네요.', '1', '<p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">제목 그대로 금융업계의 사례를 들어서</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">강의를 들을 수 있는 점이</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">공감이 되고 배울 것이 많네요.</p>                            <p><br></p>', 0, null, 'hackers', '해커스', '', '', '2021-02-23 23:50:53');
INSERT INTO BOARD (F_CATEGORY_ID, F_CATEGORY, F_LECTURE, F_TITLE, F_GRADE, F_CONTENTS, F_COUNT, F_BEST, F_ID, F_NAME, F_ATTACH_FILE_ORI, F_ATTACH_FILE_CRYPTO, F_REG_TIME) VALUES ('3', '일반직무', '사례중심의 금융상품 마케팅 기획과 전략 Skill', 's급 인재가 될 수 있을까..', '3', '<p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">강의 제목부터 눈길을 끌어서 들어봤는데</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">재밌게 잘 봤습니다.</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">물론 유익하기도 했구요..</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">다만 좀 씁쓸한건</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">안그래도 경쟁하는 사회에서 ㅜㅜㅜ</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">인재를 급으로 나눈다는게 참 씁쓸하네요..</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">그래도 어쩔 수 없이 열심히 해야겠죠...</p>                            <p><br></p>', 0, null, 'hackers', '해커스', '', '', '2021-02-23 23:51:13');
INSERT INTO BOARD (F_CATEGORY_ID, F_CATEGORY, F_LECTURE, F_TITLE, F_GRADE, F_CONTENTS, F_COUNT, F_BEST, F_ID, F_NAME, F_ATTACH_FILE_ORI, F_ATTACH_FILE_CRYPTO, F_REG_TIME) VALUES ('3', '일반직무', '알쏭달쏭 근로기준법 완전정복', '영어에 취약한 공대생 출신에게 완전 추천합니다!!', '3', '<p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px; line-height: 1.8;">공대생이라서(핑계지만...ㅎㅎ) 영어에 많이 취약합니다.<br>작년에 여름휴가로 여친이랑 홍콩에 다녀왔는데</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px; line-height: 1.8;">여친이 여친이 아니라 거의 통역사였던 것 같네요ㅠㅠ (참고로 여친은 문과생ㅎㅎㅎ)<br>회사에서 사업주프로그램인가&nbsp;해서 영어수업을 몇 개 지원해줬는데 전 기초부터 해야할 것 같아서<br>그래머 게이트웨이 베이직 과정을 들었습니다.<br>선생님이 실력이 좋으신 것도 있었지만 과정 구성 자체도 영어 초보자가 보기에 편하더군요.</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px; line-height: 1.8;">한동안 일이 바빠서 추가 과정 못 들었는데 인터미디엇 과정도 들으려구요.</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px; line-height: 1.8;">그래서 이번 여름휴가에는 괌에 가기로 했는데 자신감 좀 붙일 수 있을 것 같습니다~!</p>                            <p><br></p>', 0, null, 'hackers', '해커스', '', '', '2021-02-23 23:51:27');
INSERT INTO BOARD (F_CATEGORY_ID, F_CATEGORY, F_LECTURE, F_TITLE, F_GRADE, F_CONTENTS, F_COUNT, F_BEST, F_ID, F_NAME, F_ATTACH_FILE_ORI, F_ATTACH_FILE_CRYPTO, F_REG_TIME) VALUES ('3', '일반직무', '디자인 CEO가 알려주는 포토샵 CC', '예전부터 듣고 싶었는데 인강이 있어서 좋네요 ㅋㅋㅋ', '3', '<p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">사무직이지만 항상 디자인쪽 일을 취미로 배워보고 싶었는데 특히 몇년전부터 빅데이터가 뜨면서 인포그래픽에 대한 관심이 생겼어요</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">인포그래픽은 취미뿐 아니라 보고서 작성할 때 업무에도 쓸 수 있을 것 같아서 신청했어요</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">계속 학원으로 알아보고 있었는데 인강이 있어서 좋네요 ㅋㅋㅋ</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">인강에서 나온 주요 인포그래픽 형태 참고해서 이것저것 조심씩 만들어보고 있어요</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">딱딱한 강의도 아니고 해서 좋네요 다른 분들도 들어보시면 좋을 것 같아서 후기 남겨요~</p>                            <p><br></p>', 0, null, 'hackers', '해커스', '', '', '2021-02-23 23:51:44');
INSERT INTO BOARD (F_CATEGORY_ID, F_CATEGORY, F_LECTURE, F_TITLE, F_GRADE, F_CONTENTS, F_COUNT, F_BEST, F_ID, F_NAME, F_ATTACH_FILE_ORI, F_ATTACH_FILE_CRYPTO, F_REG_TIME) VALUES ('3', '일반직무', '영상제작PD가 알려주는 올인원 영상편집 [프리미어CC]', '저작권에 대한 인식을 바꿀 수 있는 교육', '3', '<p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">디자인업계 신입 직원입니다.</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">&nbsp;</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">최근 사내에서 저작권 관련 문제가 계속 이슈가 되고 있어서 신청했습니다.</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">&nbsp;</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">그냥 단순히 인터넷의 정보 사용할 때 어떤걸 조심해야 되는지 알고 싶어서 신청했는데,</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">&nbsp;</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">제가 있는 업계 이외에도 다양한 저작권의 형태와 권리에 대해서 배울 수 있었어요!</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">&nbsp;</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">특히 2차 저작권이나 퍼블리시티권는 매우 유용한 정보였어요</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">&nbsp;</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">좋은 과정 감사합니다 :D</p>                            <p><br></p>', 0, null, 'hackers', '해커스', '', '', '2021-02-23 23:52:07');
INSERT INTO BOARD (F_CATEGORY_ID, F_CATEGORY, F_LECTURE, F_TITLE, F_GRADE, F_CONTENTS, F_COUNT, F_BEST, F_ID, F_NAME, F_ATTACH_FILE_ORI, F_ATTACH_FILE_CRYPTO, F_REG_TIME) VALUES ('3', '일반직무', '우리 병원 차별화 전략! 병원CS의 모든 것', '4차 산업혁명에 관심을 가지고 수강', '3', '<p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">4차 산업혁명으로 많은 부분이 바뀔 것 같은데..</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">관련 지식을 쌓아보고자 해당 교육 듣고 있습니다.</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">4차 산업혁명은 여전히 어렵기만 하고, 예측이 불가능하지만</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">일단 사물인터넷이라는 기술에 대해서는 확실히 알게되어서 좋네요!</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">급격하게 변화하는 기술을 업무에서 따라잡을 수 있도록 다양한 강좌가 있었으면 하네요.</p>                            <p><br></p>', 0, null, 'hackers', '해커스', '', '', '2021-02-23 23:52:28');
INSERT INTO BOARD (F_CATEGORY_ID, F_CATEGORY, F_LECTURE, F_TITLE, F_GRADE, F_CONTENTS, F_COUNT, F_BEST, F_ID, F_NAME, F_ATTACH_FILE_ORI, F_ATTACH_FILE_CRYPTO, F_REG_TIME) VALUES ('3', '일반직무', '우리 병원 차별화 전략! 병원CS의 모든 것', '금융권 이직희망자에게는 필수(기본?)인 강의!!!', '2', '<p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">7개월간 금융권을 준비하다가 너무 힘들어서 그냥 일반 기업에 들어왔어용ㅠㅠ<br>그런데 같이 스터디했던 친구들 중 2명이 금융권 들어갔다고 알려주길래 저도 요즘 다시 금융권 이직을 준비하고 있습니당~<br>금융자격증은 투자권유대행인같은 것도 있지만 저는 개인적으로 AFPK가 가장 기본인 것 같아용~<br>그래도 이게 엄청 어렵다거나 그렇지는 않지만... 직장다니면서 독학하기엔 좀 부담스러워서ㅜㅜ</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">회사에서 직원교육이라고 공지에 뜬거 보고 마침 무료겠다 싶어 처음에 세금 신청했는데 내용도 좋았어요!</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">이제 보험이랑&nbsp;세금&nbsp;2개 들었는데 나머지도 듣고 올해 말에 시험치르려고 준비중입니당~</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">부디 내년에는 금융권에서 일하고 있는 날 영접할 수 있기를???ㅋㅋㅋㅋ</p>                            <p><br></p>', 0, null, 'hackers', '해커스', '', '', '2021-02-23 23:52:53');
INSERT INTO BOARD (F_CATEGORY_ID, F_CATEGORY, F_LECTURE, F_TITLE, F_GRADE, F_CONTENTS, F_COUNT, F_BEST, F_ID, F_NAME, F_ATTACH_FILE_ORI, F_ATTACH_FILE_CRYPTO, F_REG_TIME) VALUES ('3', '일반직무', '강성고객을 아이처럼 다스리는 CS 달인되기', '꼭 자격증 목적이 아니더라도 적극 추천하는 강의입니다.', '3', '<span style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">딱히 컴퓨터학원을 다녀본 적도 없고, 대학생때도 워드나 PPT 기본적인 기능만 썼어요ㅋㅋㅋㅋ</span><br style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;"><span style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">그런데 회사에 들어오니 생전 처음보는 워드나 PPT 기능을 사용하고</span><br style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;"><span style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">특히 엑셀을 많이 이용하더라구요... 더군다나 엑셀은 경험이 없는데ㅠㅠㅠㅠ</span><br style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;"><span style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">자격증도 워드정도만 따놔서 입사 초에 선배님들한테 엄청 혼났었는데</span><br style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;"><span style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">동기가 알려줘서 근로자카드 발급받아서 복습수강까지 하고 이제는 일하기 좀 편해졌어요!!!</span><br style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;"><span style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">자격증 목적으로 들은건 아니지만, 자격증이 목적이든 아니든 상관없이 들어도</span><br style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;"><span style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">많은 도움 얻으실 수 있다고 생각해요.</span>                            <p><br></p>', 0, null, 'hackers', '해커스', '', '', '2021-02-23 23:53:13');
INSERT INTO BOARD (F_CATEGORY_ID, F_CATEGORY, F_LECTURE, F_TITLE, F_GRADE, F_CONTENTS, F_COUNT, F_BEST, F_ID, F_NAME, F_ATTACH_FILE_ORI, F_ATTACH_FILE_CRYPTO, F_REG_TIME) VALUES ('3', '일반직무', '브랜드 가치를 높이는 호텔 직무 교육', '가볍지만 효과는 보장하는 강의', '3', '<span style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">작년 말 부서이동을 하게되면서 외국 바이어와 미팅을 할 일이 종종 생겼어요ㅠㅠ</span><br style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;"><span style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">집, 회사만 왔다갔다 하기에도 바쁜데 학원은 부담스러워서 실무영어 과정을 들었고,</span><br style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;"><span style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">지금은 미팅할 때 100%까지는 아니지만 어느정도는 알아들을 수 있네요.</span><br style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;"><span style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">다른 과정도 틈틈히 들으면 나중에는 선배님 도움 없어도 미팅에 큰 어려움은 없을 듯!!!</span><br style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;"><span style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">영어 수업이야 다른 과정도 많겠지만...</span><br style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;"><span style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">미생강의처럼 실무와 직접적으로 연관되어서 단기간에 실용적으로 써먹을 수 있는 강의는 별로 없을 것 같네요.</span><br style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;"><span style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">무엇보다도 강의 분위기가 무겁지 않고 가볍게 볼 수 있으면서도 효과도 볼 수 있어서 좋았습니다.</span>                            <p><br></p>', 0, null, 'hackers', '해커스', '', '', '2021-02-23 23:53:28');
INSERT INTO BOARD (F_CATEGORY_ID, F_CATEGORY, F_LECTURE, F_TITLE, F_GRADE, F_CONTENTS, F_COUNT, F_BEST, F_ID, F_NAME, F_ATTACH_FILE_ORI, F_ATTACH_FILE_CRYPTO, F_REG_TIME) VALUES ('3', '일반직무', '이기는 광고전략의 핵심포인트', '대화의 중요성에 대해서...', '3', '<p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">강의를 보면서 대화하는 법에 대해 많이 배우고 갑니다.</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">요새 강의들은 그림도 들어가고 만화보듯 보여주네요..</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">시간이 아깝지 않은 강의였습니다.</p>                            <p><br></p>', 0, null, 'hackers', '해커스', '', '', '2021-02-23 23:53:50');
INSERT INTO BOARD (F_CATEGORY_ID, F_CATEGORY, F_LECTURE, F_TITLE, F_GRADE, F_CONTENTS, F_COUNT, F_BEST, F_ID, F_NAME, F_ATTACH_FILE_ORI, F_ATTACH_FILE_CRYPTO, F_REG_TIME) VALUES ('3', '일반직무', '실패없는 창업 성공 시크릿 가이드', '영업 현장에 도움될 내용들이 많네요.', '3', '<p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">B2B 영업 직무 2년차입니다.</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">다른 직원들에 비해 매출이 없고 고민인 상황에서</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">배우는 심정으로 열심히 수강했습니다.</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">현장에서 듣는 강의는 시간낼 엄두도 안나고</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">몸도 피곤해서 인터넷으로 들었는데</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">내용이 꽤 유익합니다.</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">20강까지 듣는데 좀 버겁기도 했지만</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">확실히 다 듣고 나니</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">어떤 나만의 전략같은게 생긴 느낌일까요?</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">저같은 고민 있으신 분들은 추천합니다.</p>                            <p><br></p>', 0, null, 'hackers', '해커스', '', '', '2021-02-23 23:54:04');
INSERT INTO BOARD (F_CATEGORY_ID, F_CATEGORY, F_LECTURE, F_TITLE, F_GRADE, F_CONTENTS, F_COUNT, F_BEST, F_ID, F_NAME, F_ATTACH_FILE_ORI, F_ATTACH_FILE_CRYPTO, F_REG_TIME) VALUES ('3', '일반직무', '[OA국가공인자격] ITQ Master! OA기초부터 실무활용까지', '애니메이션으로 나오니까 재밌네요', '1', '<p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">샘플강의 보고 재밌겠다 싶어서 수강한 과정인데, 재밌네요 ㅋㅋ</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">&nbsp;</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">마케팅을 좀 잘 하고 싶은데 일단 뭐라도 노력해봐야 겠다는 마음으로 듣게된 강의입니다.</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">&nbsp;</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">그냥 억지로 마케팅에 대한 이론을 배우기보다는</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">&nbsp;</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">애니메이션 형태로 재미있는 상황이 연출되니</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">&nbsp;</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">배우는 입장에서도 지루하지 않게 좋은 내용을 배우게 된 것 같아 좋았습니다!!</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">&nbsp;</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">이론적인 내용들도 다시 한 번 공부해보겠습니다.</p>                            <p><br></p>', 0, null, 'hackers', '해커스', '', '', '2021-02-23 23:54:32');
INSERT INTO BOARD (F_CATEGORY_ID, F_CATEGORY, F_LECTURE, F_TITLE, F_GRADE, F_CONTENTS, F_COUNT, F_BEST, F_ID, F_NAME, F_ATTACH_FILE_ORI, F_ATTACH_FILE_CRYPTO, F_REG_TIME) VALUES ('4', '산업직무', '해외 건설계약 및 클레임해결실무', '업무능력을 한단계 키울 수 있는 강의', '2', '<p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">원래는 딱히 들을 과정이 없어서 신청했는데 잘 신청한 것 같아요 ㅎㅎ</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">한 가지 내용을 다루는 강의가 아니라서 실제 업무보면서 사용할 수 있는 다양한 스킬이랑 이론을 배울 수 있었고,</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">전체적으로 써먹을 수 있는 부분이 많아서 너무 좋네요 ㅎㅎ</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">저는 평소에 고민하던 프레젠테이션 스킬부분이랑 비즈니스 문서작성 부분의 강의에서 도움을 받이 받았어요</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">특히 비즈니스 문서작성에서 배운 내용을 실무에서 바로 연습해보고 있는데 좋네요.</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">아직 갈 길은 멀었지만 업무능력을 한 단계 끌어올릴 수 있던 시간이 되었어요. 감사합니다.</p>                            <p><br></p>', 0, null, 'hackers', '해커스', '', '', '2021-02-23 23:55:13');
INSERT INTO BOARD (F_CATEGORY_ID, F_CATEGORY, F_LECTURE, F_TITLE, F_GRADE, F_CONTENTS, F_COUNT, F_BEST, F_ID, F_NAME, F_ATTACH_FILE_ORI, F_ATTACH_FILE_CRYPTO, F_REG_TIME) VALUES ('4', '산업직무', 'Build Up! 판례로 배우는 건설분쟁', 'Biz Skill Up! 나를 깨우는 커뮤니케이션 추천합니다.', '1', '<p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">가끔 간단한 사내 커뮤니케이션에도 어려움을 느낄 때가 있어 강의를 수강하게 됬습니다.</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">&nbsp;</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">강의 구성이랑 선생님의 강의가 괜찮네요..</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">&nbsp;</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">특히 신입사원 분들이 미리 들으시면 입사해서 느끼는 커뮤니케이션 문제의 대부분은 해결 가능할 것 같네요.</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">&nbsp;</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">이제 상사나 동료 직원들과의 커뮤니케이션을 연습해보려고 합니다.</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">&nbsp;</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">강의 감사합니다~!.</p>                            <p><br></p>', 0, null, 'hackers', '해커스', '', '', '2021-02-23 23:55:31');
INSERT INTO BOARD (F_CATEGORY_ID, F_CATEGORY, F_LECTURE, F_TITLE, F_GRADE, F_CONTENTS, F_COUNT, F_BEST, F_ID, F_NAME, F_ATTACH_FILE_ORI, F_ATTACH_FILE_CRYPTO, F_REG_TIME) VALUES ('4', '산업직무', '랜섬웨어 예방 및 사이버 보안의 첫 걸음-전사원을 교육하라!', '비즈스킬업 시리즈 중 두번째로 수강한 강의..', '2', '<p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">지금 막 시험을 마지고 수강후기를 씁니다..</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">&nbsp;</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">비즈스킬 업 시리즈 강의 중에서는 커뮤니케이션 강의를 처음 듣고 내용이 괜찮아서&nbsp;수강하게 됬습니다.</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">&nbsp;</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">커뮤니케이션 강의는 신입사원부터 대리급까지 도움이 되는 강의인 반면,</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">&nbsp;</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">이 강의는 예비팀장이나 초보팀장이 들으면 도움이 될 것 같네요.</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">&nbsp;</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">강의 구성 및 내용에 100% 만족합니다.</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">&nbsp;</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">후속강의 추천 받았으면 좋겠네요...</p>                            <p><br></p>', 0, null, 'hackers', '해커스', '', '', '2021-02-23 23:55:45');
INSERT INTO BOARD (F_CATEGORY_ID, F_CATEGORY, F_LECTURE, F_TITLE, F_GRADE, F_CONTENTS, F_COUNT, F_BEST, F_ID, F_NAME, F_ATTACH_FILE_ORI, F_ATTACH_FILE_CRYPTO, F_REG_TIME) VALUES ('4', '산업직무', '장기보상! 보험영업의 열쇠', '바로 조직 구성과 관리에 활용해볼 계획', '3', '<p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">팀의 리더로써, 팀을 발전시킬수도, 후퇴시킬 수도 있는 상황에서, 제대로된 리더십을 발휘하는데 큰 도움이 되었습니다.</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">조직 패러다임에 대한 이해와 중요성을 배우고, 창조적 파괴에 대해 학습하면서 조직이 나아가야할 변화와 방향에 대해 깨달았습니다.&nbsp;</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">또한 창발적 리더의 행동원칙을 배우면서 구체적인 행동요령에 대해 익힐 수 있었습니다.</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">그렇게 해서 얻어지는 성과와 성공공식이 있다는 것을 배우게 되었고, 조직의 미래를 그릴수있게 되었습니다.</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">전체적으로 과정별로 구성이 매우 탄탄하게 되어있고 다양한 사례를 통해 이해하기 쉽게 설명하는 점이 매우 마음에 듭니다.&nbsp;</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">실질적으로 리더십에서 활용 가능한 지식 및 분석방법 등을 소개함과 동시에 과제를 통하여 리더십에 대해 분석하여봄으로써&nbsp;</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">머리로 이해한 것을 체화하는데에 도움이 되었습니다.&nbsp;</p>                            <p><br></p>', 0, null, 'hackers', '해커스', '', '', '2021-02-23 23:56:12');
INSERT INTO BOARD (F_CATEGORY_ID, F_CATEGORY, F_LECTURE, F_TITLE, F_GRADE, F_CONTENTS, F_COUNT, F_BEST, F_ID, F_NAME, F_ATTACH_FILE_ORI, F_ATTACH_FILE_CRYPTO, F_REG_TIME) VALUES ('4', '산업직무', '랜섬웨어 예방 및 사이버 보안의 첫 걸음-전사원을 교육하라!', '함께 고민하는 cs인에게 추천하고 싶은 과정', '3', '<p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">감정노동자라는 개념조차도 생소한 상태에서 학습을 시작했는데, 학습을 끝낼때 쯤..고객으로 인해 망가진 제 마음에 한줄기 빛이 들어오는 것 같았습니다.</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">현대 자본주의사회에서 감정노동자들이 왜 생기게 되었는지 알게 되었고, 지나친 과잉존대말로 인해 한글파괴가 이루어진 것에 대해 안타까움이 들었습니다.</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">감정노동자들의 정신적 피해에 대해 잘 알게 되었고 그 대응법도 익힐 수 있었습니다. 자신이 먼저 정신적으로 건강해져야 제대로 된 서비스를 할 수 있다는 것을 깨달았습니다.</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">전체적으로 과정별로 구성이 매우 탄탄하게 되어있고 다양한 사례를 통해 이해하기 쉽게 설명하는 점이 매우 마음에 듭니다.&nbsp;</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">실질적으로 서비스 업무에서 활용 가능한 지식 및 분석방법 등을 소개함과 동시에 과제를 통하여 감정노동에 대해 분석하여봄으로써&nbsp;</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">머리로 이해한 것을 체화하는데에 도움이 되었습니다.&nbsp;</p>                            <p><br></p>', 0, null, 'hackers', '해커스', '', '', '2021-02-23 23:56:29');
INSERT INTO BOARD (F_CATEGORY_ID, F_CATEGORY, F_LECTURE, F_TITLE, F_GRADE, F_CONTENTS, F_COUNT, F_BEST, F_ID, F_NAME, F_ATTACH_FILE_ORI, F_ATTACH_FILE_CRYPTO, F_REG_TIME) VALUES ('4', '산업직무', 'Build Up! 판례로 배우는 건설분쟁', 'Biz Skill up! 나를 바꾸는 서비스코칭', '4', '<p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">서비스업계에 종사하면서 고객을 대하는 일에 대해 어렵게만 느껴졌는데, 이 과정을 학습하면서 서비스 프로로 한발짝 나아가게 된것 같습니다.</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">디즈니랜드와 석봉토스트의 남다른 마인드,전략적 이미지의 효과 프로페셔널한 이미지 구축 전략,잡담의 기술, 마음을 열어주는 공감 기법, 내 편으로 만드는 칭찬 기법 등</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">적절한 사례를 통해 여러가기 기법들이 있다는 것을 배우고 현재 업무에 적용할수 있는 점이 많았습니다.</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">또한 고객의 감춰진 마음 읽기,고객의 보디랭귀지에 대한 대처방법에 대해 배우면서. 고객의 입장에서 상황을 파악하는 법도 익혔습니다.</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">공감적 경청, 공감적 대화를 배우면서 고객과 직원이 아닌 인간대 인간으로써 사람을 대하는 법을 배운 것도 참 좋았습니다.</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">단순히 서비스 수업이 아니라 인간내면을 꿰뚫어 보는 심리학 수업을 받은 느낌있었습니다.</p>                            <p><br></p>', 0, null, 'hackers', '해커스', '', '', '2021-02-23 23:56:44');
INSERT INTO BOARD (F_CATEGORY_ID, F_CATEGORY, F_LECTURE, F_TITLE, F_GRADE, F_CONTENTS, F_COUNT, F_BEST, F_ID, F_NAME, F_ATTACH_FILE_ORI, F_ATTACH_FILE_CRYPTO, F_REG_TIME) VALUES ('4', '산업직무', '해외 건설계약 및 클레임해결실무', '모든 업무를 마케팅적 사고로 진행한다면...', '3', '<p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">어렵게만 느껴졌던 마케팅에 대해 재미있는 애니메이션으로 학습할 수 있어서 도움이 많이 되었습니다.</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">마케팅 개념의 변화,마케팅 마인드의 중요성 부터 마케팅의 활용방안까지 폭넓은 범위로 심층적인 내용을 다루었던 점이 좋았습니다.</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">또한, 과정별로 구성이 매우 탄탄하게 되어있고 다양한 사례를 통해 이해하기 쉽게 설명하는 점이 매우 마음에 듭니다.&nbsp;</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">실질적으로 업무에서 활용 가능한 지식 및 분석방법 등을 소개함과 동시에 과제를 통하여 마케팅을 직접 분석하여봄으로써&nbsp;</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">머리로 이해한 것을 체화하는데에 도움이 됩니다.&nbsp;</p><p style="color: rgb(85, 85, 85); font-family: &quot;Malgun Gothic&quot;, 맑은고딕, 돋움, Dotum, sans-serif; font-size: 14px; letter-spacing: -1px;">실제로 과제를 통하여 마케팅을 분석해 봄으로써 추상적으로만 생각했던 마케팅에 대해 냉정하게 파악하고 향후 대안점을 파익해볼 수 있었습니다.&nbsp;</p>                            <p><br></p>', 1, null, 'hackers', '해커스', 'cropped_714A1445.jfif', '21bc49fa0691d760233dbb94dda7cebb.jfif', '2021-02-23 23:57:22');
